import React, { useRef } from 'react';
import { useFrame } from '@react-three/fiber';

const Butterfly = () => {
  const leftWing = useRef();
  const rightWing = useRef();

  useFrame(({ clock }) => {
    const t = clock.getElapsedTime();
    const flap = Math.sin(t * 10) * 0.5;

    if (leftWing.current) leftWing.current.rotation.y = flap;
    if (rightWing.current) rightWing.current.rotation.y = -flap;
  });

  return (
    <group position={[0, 0, 0]}>
      <mesh>
        <sphereGeometry args={[0.1, 16, 16]} />
        <meshStandardMaterial color="purple" />
      </mesh>
      <mesh ref={leftWing} position={[-0.2, 0, 0]}>
        <planeGeometry args={[0.3, 0.5]} />
        <meshStandardMaterial color="violet" side={2} />
      </mesh>
      <mesh ref={rightWing} position={[0.2, 0, 0]}>
        <planeGeometry args={[0.3, 0.5]} />
        <meshStandardMaterial color="violet" side={2} />
      </mesh>
    </group>
  );
};

export default Butterfly;
